/**
 * Toasrt.js
 * @description 提供各种toast类型提示
 * @author 康兵奎
 */
'use strict';

import React, {
	Component,
	PropTypes
} from 'react';

import {
	StyleSheet,
	View,
	Dimensions,
	Modal,
	Text,
	Image
} from 'react-native';

//定义各种提示的图标，类型有success(成功)、warning(警告)、wrong(错误)、help(帮助信息)、info(提示信息)
const _warning = 'data:image/png;base64,';
const _success = 'data:image/png;base64,';
const _wrong = 'data:image/png;base64,';
const _help = 'data:image/png;base64,';
const _info = 'data:image/png;base64,';
const _iconArray = [_success, _info, _help, _warning, _wrong];
class Toast extends Component {
	constructor(props) {
		super(props);
		this.state = {
			type: this.props.type,
			flag: false
		};
	}
	static defaultProps = {
		type: 'info',
		msg: '提示信息',
		timeout: 2000
	};
	static propTypes = {
		type: PropTypes.oneOf(['success', 'info', 'help', 'warning', 'wrong']).isRequired, //类型
		msg: PropTypes.string.isRequired, //提示信息
		timeout: PropTypes.number //关闭时间，默认2000毫秒
	}

	open = () => {
		this.setState({
			flag: true
		});
		setTimeout(() => {
			this.setState({
				flag: false
			});
		}, this.props.timeout);
	}
	getIconUri = () => {
		const {
			type
		} = this.props;
		const _arr = ['success', 'info', 'help', 'warning', 'wrong'];
		return _iconArray[_arr.indexOf(type)];
	}
	render() {
		return (
			<Modal
	          animationType={"none"}
	          transparent={true}
	          visible={this.state.flag}
	          onRequestClose={() => {}}
	          >
		        <View style={[styles.loadingView]}>
			        <View style={styles.loadItem}>
			            <Image 
			            	source={{uri: this.getIconUri()}}
			            	resizeMode={Image.resizeMode.cover}
		          			style={styles.thumbnail}/>
			            <Text style={styles.loadText}>{this.props.msg}</Text>
			        </View>
			    </View>
	        </Modal>
		);
	}
}
const _width = Dimensions.get('window').width;
const _height = Dimensions.get('window').height;
const styles = StyleSheet.create({
	loadingView: {
		flex: 1,
		height: _height,
		width: _width,
		position: 'absolute',
		alignItems: 'center',
		justifyContent: 'center',
	},
	thumbnail: {
		width: 50,
		height: 50,
		marginTop: 20,
		marginLeft: 35,
	},
	loadItem: {
		width: 120,
		height: 120,
		backgroundColor: '#5c5c5c',
		borderRadius: 10,
	},
	loadText: {
		color: '#fff',
		fontSize: 20,
		width: 120,
		marginTop: 3,
		textAlign: 'center'
	}
});


export default Toast;